function centerText(){var a,b,c;$(".text-container").each(function(){a=$(this).outerHeight(),b=$(this).find(".text-wrap").outerHeight(),c=(a-b)/2,$(this).find(".text-wrap").css({marginTop:c,marginBottom:c})})}$(window).load(function(){centerText(),$(".lazy").lazyload({effect:"fadeIn",threshold:500}),$(".menu-toggle").click(function(){$(".main-navigation").toggleClass("toggled")})});